import { Component, Input } from '@angular/core';

import { Twimp } from '../twimp.model';

@Component({
  selector: 'tweempus-twimp-card',
  templateUrl: './twimp-card.component.html',
  styleUrls: ['./twimp-card.component.css']
})
export class TwimpCardComponent {
  @Input() twimp!: Twimp;
}
<section class="twimp">

	<div class="twimp_header">
		<img class="twimp_header_img" [src]="twimp.author.image" alt="Imagen de un twimp">
		<a class="twimp_header_name" [href]="twimp.author.url" target="_b">by {{twimp.author.fullName}}</a>
		<div class="twimp_header_date">Published on {{twimp.timestamp}}</div>
	</div>

	<div class="twimp_content">
		{{twimp.content}}
	</div>

	<hr class="twimp_line">

	<div class="twimp_fav">
		<a class="fav_icon fav_icon-selected" href="#">&#10084;</a>
	</div>

</section>
